parameters.jsCode:
// Multi-AI Response Analyzer and Comparator
const query = $node['Research Query Input'].json.query || 'What are the latest developments in AI?';

// Extract responses from each AI
const perplexityResponse = $input.first().json.choices[0].message.content;
const chatgptResponse = $input.item(1).json.choices[0].message.content;
const claudeResponse = $input.last().json.content[0].text;

// Analyze response characteristics
function analyzeResponse(text, source) {
  return {
    source: source,
    length: text.length,
    wordCount: text.split(/\s+/).length,
    sentenceCount: text.split(/[.!?]+/).length - 1,
    hasLinks: text.includes('http'),
    hasCitations: text.includes('[') || text.includes('('),
    tone: analyzeTone(text),
    keyTopics: extractKeyTopics(text)
  };
}

function analyzeTone(text) {
  const lowerText = text.toLowerCase();
  if (lowerText.includes('exciting') || lowerText.includes('promising') || lowerText.includes('breakthrough')) {
    return 'optimistic';
  } else if (lowerText.includes('challenge') || lowerText.includes('concern') || lowerText.includes('risk')) {
    return 'cautious';
  } else {
    return 'neutral';
  }
}

function extractKeyTopics(text) {
  const keywords = ['AI', 'machine learning', 'neural network', 'deep learning', 'automation', 'technology', 'algorithm'];
  return keywords.filter(keyword => text.toLowerCase().includes(keyword.toLowerCase()));
}

// Calculate similarity between responses
function calculateSimilarity(text1, text2) {
  const words1 = new Set(text1.toLowerCase().split(/\W+/));
  const words2 = new Set(text2.toLowerCase().split(/\W+/));

  const intersection = new Set([...words1].filter(x => words2.has(x)));
  const union = new Set([...words1, ...words2]);

  return Math.round((intersection.size / union.size) * 100);
}

// Generate comprehensive analysis
const analysis = {
  query: query,
  timestamp: new Date().toISOString(),
  responses: {
    perplexity: {
      text: perplexityResponse,
      analysis: analyzeResponse(perplexityResponse, 'Perplexity')
    },
    chatgpt: {
      text: chatgptResponse,
      analysis: analyzeResponse(chatgptResponse, 'ChatGPT')
    },
    claude: {
      text: claudeResponse,
      analysis: analyzeResponse(claudeResponse, 'Claude')
    }
  },
  comparison: {
    similarities: {
      perplexity_vs_chatgpt: calculateSimilarity(perplexityResponse, chatgptResponse),
      perplexity_vs_claude: calculateSimilarity(perplexityResponse, claudeResponse),
      chatgpt_vs_claude: calculateSimilarity(chatgptResponse, claudeResponse)
    },
    averageSimilarity: Math.round(
      (calculateSimilarity(perplexityResponse, chatgptResponse) +
       calculateSimilarity(perplexityResponse, claudeResponse) +
       calculateSimilarity(chatgptResponse, claudeResponse)) / 3
    ),
    mostDetailed: {
      byLength: [perplexityResponse, chatgptResponse, claudeResponse]
        .map((text, index) => ({ text, length: text.length, source: ['Perplexity', 'ChatGPT', 'Claude'][index] }))
        .sort((a, b) => b.length - a.length)[0].source,
      byWordCount: [perplexityResponse, chatgptResponse, claudeResponse]
        .map((text, index) => ({ text, wordCount: text.split(/\s+/).length, source: ['Perplexity', 'ChatGPT', 'Claude'][index] }))
        .sort((a, b) => b.wordCount - a.wordCount)[0].source
    }
  },
  insights: {
    consensus: calculateSimilarity(perplexityResponse, chatgptResponse) > 60 && 
              calculateSimilarity(perplexityResponse, claudeResponse) > 60 && 
              calculateSimilarity(chatgptResponse, claudeResponse) > 60 ? 'High' : 'Low',
    uniquePerspectives: [
      perplexityResponse.includes('http') ? 'Perplexity provides external sources' : null,
      chatgptResponse.includes('examples') ? 'ChatGPT provides examples' : null,
      claudeResponse.includes('analysis') ? 'Claude provides deep analysis' : null
    ].filter(Boolean)
  }
};

return analysis;

